//设置cookie
function setCookie(name, value) {
 var exp = new Date();
 exp.setTime(exp.getTime() + 30 * 8640000);

}
//读取cookie
function getCookie(name) {
 var arr = document.cookie.match(new RegExp("(^| )" + name + "=([^;]*)(;|$)"));
 if (arr != null) return unescape(arr[2]);
 return "";
}
//屏蔽右键菜单
